      <Sidebar chapters={mockChapters} currentChapterId={params.chapterId} />
      <main className="flex-1 p-8">
        <header>
          <h1>{chapter.title}</h1>
          <p>{chapter.description}</p>
          <ProgressBar completed={completedCount} total={chapter.episodes.length} />
        </header>
        <div className="space-y-4 mt-8">
          {chapter.episodes.map(episode => (
            <EpisodeCard key={episode.id} episode={episode} />
          ))}
        </div>
      </main>
    </div>
